nta:
	/**
** CHANNELS
**/

// Spindle speed ranges
const int N_W = 59; 
chan l, u, i_0, m[N_W];

/**
** TRACE
**/ 
const int STOP = -1;
const int LOAD = -2;
const int UNLOAD = -3;

// Number of events
const int N_E = **N_EVENTS**
const int TRACE[N_E][2] = **TRACE**
const int TAU = **TIME_BOUND**

/**
** UTILITY FUNCTIONS
**/

double max(double x,double y) {
    if(x>=y) return x;
    else return y;
}


		Program
		clock x;

int T = 0;
int next_i = -1;
int next_e = -1;

void get_next() {
    next_i++;
    if(next_i>=N_E) return;
    next_e = TRACE[next_i][0];
    T = TRACE[next_i][1];
}

			working
			x<=T


			_init_



			new_e



			COMPLETE





			next_i>=N_E




			next_i<N_E && next_e!=STOP
			m[next_e]!
			x=0, get_next()





			next_i<N_E && next_e==STOP
			i_0!
			x=0, get_next()





			next_i<N_E && next_e==LOAD
			l!
			x=0, get_next()





			next_i<N_E && next_e==UNLOAD
			u!
			x=0, get_next()





			x>=T




			x=0, get_next()


**MACHINE**

p_1 = Program();
m_1 = Machine();

system p_1, m_1;



			simulate[<=TAU]{m_1.w, m_1.P}
